_sequence = [0,1,2,1]

def get_frames():
	frames = []
	for i in _sequence:
		frames.append(Image.fromarray(_frames[i]*255, 'L').convert('1'))
	return frames

def play(d, event):
	while not event.is_set():
		d.display(frames[0])
		event.wait(10)
		for i in range(1,len(frames)):
			if not event.is_set():
				with regulator:
					d.display(frames[i])

frames = get_frames()
regulator = framerate_regulator(fps=0)
